import { CHANGE_OFF, CHANGE_ON } from "../types";

const initialState = {
  change: false,
};

export const buttonReducer = (state = initialState, action) => {
  console.log("reducer state>>>", state);
  switch (action.type) {
    case CHANGE_ON:
      return {
        ...state,
        change: true,
      };

    case CHANGE_OFF:
      return {
        ...state,
        change: false,
      };

    default:
      return state;
  }
};
