import { INPUT_OFF, INPUT_ON } from "../types";

const initialState = {
  click: false,
};

export const inputReducer = (state = initialState, action) => {
  switch (action.type) {
    case INPUT_ON:
      return {
        ...state,
        click: true,
      };

    case INPUT_OFF:
      return {
        ...state,
        click: false,
      };

    default:
      return state;
  }
};
